SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;c'est aussi « la » cellule endocrine du placenta, capable de secreter des hormones steroides et des hormones polypeptidiques determinantes pour la suite de la grossesse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;progesterone  : relaxation du muscle uterin&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lire le reste&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;hcg : maintien du corps jaune , stimule la differenciation du trophoblaste.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;hpl (human placental lactogene) : antagoniste de l'insuline sur le metabolisme maternel favorisant ainsi l'apport de nutriments au fœtus. + effet somatotrope sur la mere (donc reserves pour fœtus… on ne mange pas pour 2 !)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lire le reste&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
