SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le volume de la chambre intervilleuse represente 40 % du volume placentaire total, soit 250 ml a terme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle se remplit deux a trois fois par minute.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce sang est &lt;/span&gt;&lt;span  fontweight='bold'&gt;une reserve&lt;/span&gt;&lt;span &gt; utilisable par le fœtus en cas d'arret de la circulation placentaire physiologique ou pathologique. le fœtus dispose ainsi d'une &lt;/span&gt;&lt;span  fontweight='bold'&gt;reserve en oxygene de 60 a 90 secondes&lt;/span&gt;&lt;span &gt;, ce delai est moindre en cas d'insuffisance placentaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les arteres iliaques internes fœtales donnent chacune une artere ombilicale =&amp;gt; cordon =&amp;gt; se divise en plusieurs branches dans le chorion =&amp;gt; penetrent dans les troncs villositaires : &lt;/span&gt;&lt;span  fontweight='bold'&gt;arteres cotyledonaires&lt;/span&gt;&lt;span &gt;. le sang est disperse dans un reseau extremement riche qui penetre les moindres divisions villositaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les veines cotyledonaires se collectent dans une veine ombilicale unique et le sang regagne finalement le &lt;/span&gt;&lt;span  fontweight='bold'&gt;systeme cave inferieur du fœtus&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;le sang maternel, arrivant des arteres spiralees sous pression elevee&lt;/span&gt;&lt;span &gt; (70 a 80 mm de hg), est projete par saccades dans la chambre intervilleuse en jets qui viennent frapper la plaque choriale. de la, il diffuse et revient vers la plaque basale et est aspire par les veines utero-placentaires ou la pression est encore plus faible (8 mm de hg) que dans la chambre intervilleuse (10 mm de hg). le flux sanguin maternel tres important est estime a 500 a 600 ml par minute. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
