SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vers la 3&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; semaine, le chorion villeux est forme a partir des st + cytotrophoblastes. cette&lt;/span&gt;&lt;span &gt; periode est marquee par le &lt;/span&gt;&lt;span &gt;dvlpt&lt;/span&gt;&lt;span &gt; cavite amniotique, limitee&lt;/span&gt;&lt;span &gt; par les &lt;/span&gt;&lt;span &gt;amnioblastes&lt;/span&gt;&lt;span &gt; et vesicule &lt;/span&gt;&lt;span &gt;vitteline&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans la cavite choriale se trouve donc&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;le cœlome externe, dans lequel l'embryon , son amnios et son sac vitellin sont suspendus par le pedicule embryonnaire qui deviendra le cordon ombilical&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
