SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une&lt;/span&gt;&lt;span &gt; semaine plus tard, le &lt;/span&gt;&lt;span &gt;disque embryonnaire s'est etendu et replie&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cavite amniotique est bien sur le « dos » de l'embryon&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la creation de la &lt;/span&gt;&lt;span &gt;vesicule&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;vitteline&lt;/span&gt;&lt;span &gt; secondaire va former un pedicule&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;vittelin&lt;/span&gt;&lt;span &gt;. la vesicule &lt;/span&gt;&lt;span &gt;vitteline&lt;/span&gt;&lt;span &gt; primaire va donner naissance a l'allantoide&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un deuxieme&lt;/span&gt;&lt;span &gt; pedicule, auquel est suspendu l'embryon au chorion, existe : &lt;/span&gt;&lt;span &gt;pedicule&lt;/span&gt;&lt;span &gt; embryonnaire ou se trouve l'&lt;/span&gt;&lt;span &gt;allantoide&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces deux pedicules vont creer le cordon ombilical&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous voyez que les villosites ont regresse a certains endroit. ce chorion lisse ainsi cree sera la future membrane externe de l'œuf. &lt;/span&gt;&lt;span &gt;cf&lt;/span&gt;&lt;span &gt; cours 2 pour savoir pourquoi les villosites ont regresse ici&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
