SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;toujours a la 8&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; semaine, l'œuf implante dans l'endometre se developpe dans la cavite&lt;/span&gt;&lt;span &gt; uterine et va prendre de plus en plus de place.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la caduque est ce que l'endometre est devenu depuis la nidation. on parle de caduque basilaire a l'endroit ou le chorion villeux s'est implante ; de c. reflechie a l'endroit ou le chorion lisse se trouve ; de c. parietale a l'oppose de la cavite amniotique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est vers la 12&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; semaine que caduque reflechie et parietale vont fusionner : la cavite uterine sera alors completement remplie par l'œuf.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
