SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lorsque que&lt;/span&gt;&lt;span &gt; la division se fait entre 2 et 8 jours apres la fecondation, au stade morula ou blastocyste et avant la formation de l'amnios, chaque fœtus aura sa propre cavite amniotique, mais il n'y a qu'une seule masse placentaire avec des anastomoses vasculaires possibles entre les circulations des 2 fœtus.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la gg mcba represente environ 70% des gg mz&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
