SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;arteres issues de l'aorte&lt;/span&gt;&lt;span &gt; et s'enroulent autour de la veine. il existe un effet de pompe des arteres sur la veine a cause des spires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a quelques cm du placenta, elles anastomosent afin d'egaliser les flux dans le cadre de la distribution du sang dans la structure placentaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;flux sanguin fœtal a environ 500 ml/min&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;debit ombilical represente 40% du debit cardiaque fœtal, mais il varie en fonction des rythmes nycthemeraux, de la pression arterielle, du rythme cardiaque et des mouvements respiratoires fœtaux (qui modifient le rcf)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;fermeture du cordon : contraction de certaines fibres musculaires par phenomenes chimiques et mecaniques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;etirement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;oxygene&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;adrenaline, noradrenaline, vasopressine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;temperature&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
