SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;98% d'eau&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;densite&lt;/span&gt;&lt;span &gt; moyenne 1,006&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ph entre 7,10 et 7,20&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plutot hypo-&lt;/span&gt;&lt;span &gt;osmolaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;composition ionique varie au cours de la grossesse (20sa : &lt;/span&gt;&lt;span &gt;keritinisation&lt;/span&gt;&lt;span &gt; empeche la diffusion a travers la peau : passage renal modifie l'&lt;/span&gt;&lt;span &gt;osmolalite&lt;/span&gt;&lt;span &gt; (faite a 98% grace a l'ion na)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;outre le na qui diminue en approchant du terme, les autres cations et anions, de meme que les oligoelements ne presentent pas de variation au cours de la grossesse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tous les aa sont presents dans le la&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la source urinaire de la implique l'augmentation de la concentration au cours de la grossesse, de l'acide urique de l'uree et de la creatinine presents dans le la.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs&lt;/span&gt;&lt;span &gt; familles d'enzymes sont retrouvees dans le la. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;diamineoxydase&lt;/span&gt;&lt;span &gt; : a &lt;/span&gt;&lt;span &gt;lgtps&lt;/span&gt;&lt;span &gt; permis le diagnostic de rpm (autres moyens : mesure du ph vaginal, maintenant : presence igf-bp1 ou pamg-1 &lt;/span&gt;&lt;span &gt;placental&lt;/span&gt;&lt;span &gt; alpha &lt;/span&gt;&lt;span &gt;microglobuline&lt;/span&gt;&lt;span &gt; 1)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il y a donc aussi des facteurs de croissance et des proteines&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;phospholipides : role dans la composition du surfactant. rapport lecithine/&lt;/span&gt;&lt;span &gt;sphingomyeline&lt;/span&gt;&lt;span &gt; &amp;gt;2 est associe a une bonne maturation pulmonaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cellules epitheliales fœtales desquamees et des cellules fibroblastiques (permettent d'acceder au patrimoine genetique fœtal : dan)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
