SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;hydramnios : principalement du a un defaut d'elimination&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;donc : &lt;/span&gt;&lt;span &gt;atresie des choanes, atresie œsophage, fistule &lt;/span&gt;&lt;span &gt;oesophago&lt;/span&gt;&lt;span &gt;-tracheale, deglutition absente : neurologique ou medicamenteux (anti epileptiques)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus generalement : retentissement sur transit gastrique (fente labiale, imperforation anale)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mais aussi si augmentation de production non compensee : polyurie (anomalie du rein, avc &lt;/span&gt;&lt;span &gt;foetaux&lt;/span&gt;&lt;span &gt;, diabete maternel)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;defaut de production: &lt;/span&gt;&lt;span  fontweight='bold'&gt;oligo&lt;/span&gt;&lt;span  fontweight='bold'&gt;-amnios&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; oligurie/ insuffisance renale / agenesie renale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; obstruction du tractus urinaire (valves de l'uretre posterieur)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; insuffisance placentaire (hypovolemie et hypoxie qui diminuent le metabolisme fœtal : defaut d'urine et de secretion pulmonaire)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; absence de secretion pulmonaire (anomalies chromosomiques)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;rpm&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;prise medicamenteuse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;risque augmentes de &lt;/span&gt;&lt;span &gt;morbimortalite&lt;/span&gt;&lt;span &gt; fœtale : soit a cause de l'etiologie de l'anomalie de qla, soit a cause de la deterioration des roles du la (distension uterine dans ha par exemple, ou defaut d protection mecanique dans &lt;/span&gt;&lt;span &gt;oligoa&lt;/span&gt;&lt;span &gt;)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
