SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bonjour,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je suis lionel di marco, sage-femme et enseignant au departement de maieutique de l'ufr de medecine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;aujourd'hui, dans le cadre de l'etude de l'unite foeto-placentaire, nous allons voir comment se realisent les echanges foeto-maternels. l'objectif de ce cours est de comprendre les differents mecanismes entrant en jeu dans ce systeme d'echange, et de determiner les mecanismes utilises dans certains echanges.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous verrons donc en premier lieu les caracteristiques des differents modes de diffusion des molecules a travers la barriere placentaire ; puis nous analyserons les certains des transferts necessaires au bon fonctionnement de l'unite foeto-placentaire, et au developpement du fœtus.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@mecanismes de transferts@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 4;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
