SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour expliquer cela, utilisons les superbes photos de lennart nilsson qui montrent bien qu'en debut de developpement,&lt;/span&gt;&lt;span &gt; le chorion villeux, que l'on appelle chevelu comme vous pouvez le constater, entoure completement l'œuf.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;*clic*&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'oxygene est la clef de cette explication. les&lt;/span&gt;&lt;span &gt; trophoblastes existent au depart tout autour de l'embryon.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;*clic*&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pourtant un petit defaut de permeabilite dans la coque trophoblastique va permettre l'oxygenation d'une zone concentrique autour du cordon ombilical, au premier trimestre. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;*clic*&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
