SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les membranes &lt;/span&gt;&lt;span &gt;vasculo-syncytiales&lt;/span&gt;&lt;span &gt; sont localisees dans le bout terminal des villosites choriales ou les capillaires sont dilates en sinusoides. dans cette zone, l'epaisseur de la barriere separant la circulation fœtale de la chambre &lt;/span&gt;&lt;span &gt;intervilleuse&lt;/span&gt;&lt;span &gt;, et donc du sang maternel, est diminuee jusqu'a avoir une epaisseur de 1 a 2 micrometres. ce sont donc les zones d'echanges privilegiees entre les 2 circulations.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
