SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le placenta qui joue un &lt;/span&gt;&lt;span &gt;role&lt;/span&gt;&lt;span &gt; de ≪ poumon &lt;/span&gt;&lt;span &gt;foetal&lt;/span&gt;&lt;span &gt; ≫ est 15 fois moins efficace (a poids tissulaire &lt;/span&gt;&lt;span &gt;equivalent&lt;/span&gt;&lt;span &gt;) que le poumon &lt;/span&gt;&lt;span &gt;reel&lt;/span&gt;&lt;span &gt; . il reste efficace &lt;/span&gt;&lt;span &gt;grace&lt;/span&gt;&lt;span &gt; a la grande surface d'&lt;/span&gt;&lt;span &gt;echanges&lt;/span&gt;&lt;span &gt; et permet donc l'apport de &lt;/span&gt;&lt;span &gt;dioxygene&lt;/span&gt;&lt;span &gt; au &lt;/span&gt;&lt;span &gt;foetus&lt;/span&gt;&lt;span &gt; et l'&lt;/span&gt;&lt;span &gt;evacuation&lt;/span&gt;&lt;span &gt; du dioxyde de carbone &lt;/span&gt;&lt;span &gt;foetal&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a terme le &lt;/span&gt;&lt;span &gt;foetus&lt;/span&gt;&lt;span &gt; puise 20 a 30 ml de &lt;/span&gt;&lt;span &gt;dioxygene&lt;/span&gt;&lt;span &gt; par minute dans la circulation maternelle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'oxygenation fœtale est facilite par les &lt;/span&gt;&lt;span &gt;differences&lt;/span&gt;&lt;span &gt; de concentration et de pression partielle entre les circulations, mais aussi par l'&lt;/span&gt;&lt;span &gt;affinite&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;elevee&lt;/span&gt;&lt;span &gt; de l'&lt;/span&gt;&lt;span &gt;hbf&lt;/span&gt;&lt;span &gt; avec l'o2. ce qui permet un transfert de la mere au fœtus plus aise.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin l&lt;/span&gt;&lt;span &gt;'effet bohr correspond a l'effet du ph sur la courbe de dissociation de l'oxygene de l'hemoglobine. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque le ph est bas, l'affinite de l'o2 pour l'hemoglobine est diminue. or, le ph est en rapport etroit avec &lt;/span&gt;&lt;span &gt;la pression partielle du co2, puisque l'augmentation de cette derniere est liee a une diminution du ph.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ainsi, comme le sang fœtal n'est pas tres sature,&lt;/span&gt;&lt;span &gt; le ph&lt;/span&gt;&lt;span &gt; est bas, et donc la diffusion de l'o2 vers les tissus &lt;/span&gt;&lt;span &gt;foetaux&lt;/span&gt;&lt;span &gt; depuis l'&lt;/span&gt;&lt;span &gt;hb&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;foetale&lt;/span&gt;&lt;span &gt; est facilitee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
