SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la fonction&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;endrocine&lt;/span&gt;&lt;span &gt; du placenta permets de secreter &lt;/span&gt;&lt;span &gt;2 types d'hormones&lt;/span&gt;&lt;span &gt; : &lt;/span&gt;&lt;span &gt;steroides&lt;/span&gt;&lt;span &gt; (lipidiques et donc hydrophobes, necessitent proteine de transport pour circuler) et peptidiques (petites proteines, et donc hydrophiles et donc hydrosolubles, circulent librement jusqu'au cellules cibles)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous voyez ici les principales :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'&lt;/span&gt;&lt;span &gt;hcg&lt;/span&gt;&lt;span &gt; dont le pic se situe en debut de grossesse&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la progesterone qui ne cesse d'augmenter de la fin du premier trimestre jusqu'a l'accouchement ou elle chute legerement, et 3 jours apres la naissance, ou elle va s'effondrer&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les &lt;/span&gt;&lt;span &gt;oestrogenes&lt;/span&gt;&lt;span &gt; (estradiol, &lt;/span&gt;&lt;span &gt;estriol&lt;/span&gt;&lt;span &gt; et estrone) et l'&lt;/span&gt;&lt;span &gt;hpl&lt;/span&gt;&lt;span &gt; qui ont une secretion en constante augmentation&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
