SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;toute la definition&lt;/span&gt;&lt;span &gt; du titre de cette unite d'enseignement pourrait etre explicitee dans ce schema&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;durant les premiers mois l'hcg secretee par le st stimule les surrenales de l'enfant et le corps jaune maternel&lt;/span&gt;&lt;span &gt; afin d'induire la &lt;/span&gt;&lt;span &gt;secretion de &lt;/span&gt;&lt;span &gt;dehydroepiandrosterone&lt;/span&gt;&lt;span &gt; (&lt;/span&gt;&lt;span &gt;dhea) , d'estrogenes et de progesterone.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la dhea secretee va ensuite servir de precurseur a la synthese d'&lt;/span&gt;&lt;span &gt;oestrogenes&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la&lt;/span&gt;&lt;span &gt; progesterone, quand a elle, est fabriquee a partir du cholesterol grace a la stimulation de l'hcg, a la fois par le fœtus et par le placenta&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
