SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ok&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en regard des espaces definis par le 3&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; intercostal jusqu'au 6&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt; intercostal ; et des cotes qui les definissent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le mamelon se projette en regarde de d9&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par rapport a la ligne axilaire, l'areole se projete 5cm au dessous de celle-ci&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par rapport au milieu de l'humerus, l'areole se projette 2 cm au dessous&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'areloe se trouve 1cm a l'interieur par rapport au milieu de la clavicule&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les 2 sont separes d'environ 20cm&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au niveau des rapports musculaires, chaque sein repose sur le muscle pectoral.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
