SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontweight='bold'&gt;ok&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;glande mammaire = glande exocrine, tubulo-alveolaire composee, secretant le lait.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;• &lt;/span&gt;&lt;span  fontweight='bold'&gt;architecture = celle des autres glandes exocrines composees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;systeme ramifie de canaux excreteurs termines en cul-de-sac par&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;des portions secretrices tubulo-alveolaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;10 a 20 lobes/ glande mammaire, draines par des canaux&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;galactophores collecteurs « lactiferes » s'ouvrant individuellement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;a la peau au niveau du mamelon.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;• &lt;/span&gt;&lt;span  fontweight='bold'&gt;chaque lobe = constitue de lobules, chaque lobule etant constitue de&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;tubulo-alveoles correspondant a la partie secretrice de la glande.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;• &lt;/span&gt;&lt;span  fontweight='bold'&gt;lobes separes / tissu conjonctif dense et entoure / tissu adipeux&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;abondant&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;• &lt;/span&gt;&lt;span  fontweight='bold'&gt;les lobules sont entoures / tissu conjonctif dense&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;• &lt;/span&gt;&lt;span  fontweight='bold'&gt;tissu conjonctif intralobulaire (tissu conjonctif palleal) = lache.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
