SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ok&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le tissu change au cours de la vie : a la puberte, durant les cycles menstruels, lors d'une grossesse et de l'allaitement, et apres la menopause.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;schema : petite fille / jeune femme / femme enceinte / femme allaitante / apres sevrage&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;modif majeures pdt puberte ; puis grossesse ; et tres peu pdt allaitement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vers 10 a 12 ans, la glande reagit aux changements hormonaux et commence a se developper, ramifiant son reseau galactophore. il y a plus de tissu glandulaire chez les femmes jeunes. chez la femme plus agee, ce tissu est en partie remplace par du tissu graisseux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
