SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span  fontstyle='italic'&gt;ok&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontstyle='italic'&gt;chez la fille&lt;/span&gt;&lt;span &gt; : tanner a decrit la	 clinique des changements morphologiques. il a divise le developpement du sein en cinq stades.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;stade 1 : prepubertaire : saillie simple du mamelon. avant la puberte, la glande mammaire subit une croissance tres lente par ramifications successives de galactophores&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;stade 2 : apparition du bourgeon mammaire, soulevement du sein et de l'areole. le diametre de celui-ci augmente (9-10 ans).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;stade 3 : elargissement de la saillie du sein et de l'areole s'accentue mais leurs contours ne sont pas distincts (11-12 ans).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;stade 4 : projection en avant de l'areole et du mamelon qui forment une saillie distincte de celle du sein (13 ans).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;stade 5 : sein adulte hemispherique. seul le mamelon fait saillie, l'areole se confond avec le contour general du sein (15-16 ans).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce processus de maturation s'etend sur une periode d'environ quatre ans. le stade 1 precede en general de six mois la pilosite pubienne et d'environ deux ans la menarche.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontstyle='italic'&gt;chez le garcon&lt;/span&gt;&lt;span &gt; : a la puberte on note surtout un elargissement des areoles, sans accentuation de la pigmentation. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

