SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour faire office de bilan, je vous presente les principales conclusions d'u&lt;/span&gt;&lt;span &gt;ne expertise scientifique collective menee par l'inra et le cemagref, a la demande des ministeres en charge de l'agriculture et de l'environnement, et qui a permis de dresser un etat des lieux des connaissances sur lesquelles pourraient se fonder des actions visant a reduire le niveau actuel d'utilisation des pesticides et leurs impacts environnementaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
