SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le developpement de l'agriculture bio en france est marque par quelques dates importantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en 1972, nature et progres pose les bases objectives de l'attribution de la mention « bio » avec le premier cahier des charges. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en 1981 : l'agriculture bio est reconnu en france par la loi d'orientation generale sur l'agriculture. a noter que la france est le premier pays europeen a avoir reconnu l'agriculture bio.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les cahiers des charges definissent avec precision tous les elements de la chaine allant du sol a l'assiette qui vont permettre de fournir des aliments bio certifies. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;puis, il y aura une reconnaissance au niveau europeen d'abord avec les produits vegetaux en 1991 puis avec les produits d'origine animale en 1999. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
