SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;j'ai essaye ici de resumer les points forts et les points faibles de ce type d'agriculture. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;bien sur, ce type d'agriculture a permis d'augmenter les rendements, d'ameliorer certains aspects au niveau qualite et de diminuer la part de l'alimentation dans le budget des menages.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ceci dit, on ne peut pas nier que ces pratiques ont eu un impact non negligeable sur l'environnement en entrainant de la pollution, une baisse de la fertilite des sols, en degradant les paysages… et puis, elles ont donne lieu egalement a un certain nombre de scandales sanitaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
