SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici un schema qui resume les points de la reglementation que je viens de citer. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce qu'il vous faut retenir : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sont etiquetes ogm les aliments qui contiennent plus de 0,9% d'ogm sauf …..&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sont etiquetes sans ogm, les produits non transformes contenant moins d'une certaine quantite d'ogm qui est de 0,1 ou 0,9% selon le produits. je ne vous demande pas de retenir le detail de ces produits mais d'avoir en tete que le sans ogm n'est pas un 0 ogm mais un inferieur a x%. c'est je pense  extremement important de comprendre ca si on veut etre un consommateur eclaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les produits issus de l'agriculture biologique sont sans ogm, y compris certains labels&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
