SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la &lt;/span&gt;&lt;span  fontweight='bold'&gt;bouche&lt;/span&gt;&lt;span &gt; ou &lt;/span&gt;&lt;span  fontweight='bold'&gt;cavite orale&lt;/span&gt;&lt;span &gt; est une cavite irreguliere qui represente le segment initial du tube digestif. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle contient la &lt;/span&gt;&lt;span  fontweight='bold'&gt;langue* &lt;/span&gt;&lt;span &gt;au centre  et les &lt;/span&gt;&lt;span  fontweight='bold'&gt;dents* &lt;/span&gt;&lt;span &gt;avec l'arcade dentaire superieure et inferieure. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la cavite orale est impliquee  dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;deglutition&lt;/span&gt;&lt;span &gt;, &lt;/span&gt;&lt;span  fontweight='bold'&gt;phonation&lt;/span&gt;&lt;span &gt; et l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;articulation des sons&lt;/span&gt;&lt;span &gt;. elle est le siege du &lt;/span&gt;&lt;span  fontweight='bold'&gt;gout.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette cavite est ouverte a ses deux extremites:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	- elle communique en avant avec le milieu exterieur au niveau de l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;orifice buccal&lt;/span&gt;&lt;span &gt; circonscrit par les &lt;/span&gt;&lt;span  fontweight='bold'&gt;levres*&lt;/span&gt;&lt;span &gt;. la depression mediane au dessus de la levre superieure correspond au &lt;/span&gt;&lt;span  fontweight='bold'&gt;philtrum*&lt;/span&gt;&lt;span &gt;.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	- et communique en arriere avec l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;oro-pharynx*&lt;/span&gt;&lt;span &gt;  au niveau de ce que l'on appel l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;isthme du gosier&lt;/span&gt;&lt;span &gt;. c'est a ce niveau que ce trouve les f&lt;/span&gt;&lt;span  fontweight='bold'&gt;osses tonsilaire*&lt;/span&gt;&lt;span &gt; (siege des &lt;/span&gt;&lt;span  fontweight='bold'&gt;tonsiles palatines&lt;/span&gt;&lt;span &gt; ou amygdales palatines).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les limites laterale corresponde au &lt;/span&gt;&lt;span  fontweight='bold'&gt;joues&lt;/span&gt;&lt;span &gt;, la limite inferieure correspond au &lt;/span&gt;&lt;span  fontweight='bold'&gt;plancher buccal&lt;/span&gt;&lt;span &gt; et la limite superieur  correspond a la region palatine ou &lt;/span&gt;&lt;span  fontweight='bold'&gt;palais*&lt;/span&gt;&lt;span &gt; qui se prolonge sur la ligne mediane par la&lt;/span&gt;&lt;span  fontweight='bold'&gt; luette* ou uvule.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
