SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;sur une coupe sagitale sont mis en place :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la &lt;/span&gt;&lt;span  fontweight='bold'&gt;cavite orale*&lt;/span&gt;&lt;span &gt; , occupee en quasi-totalite par la langue&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les &lt;/span&gt;&lt;span  fontweight='bold'&gt;fosses nasales*&lt;/span&gt;&lt;span &gt; au dessus qui vont filtrer, rechauffer et humidifier l'air inspire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le&lt;/span&gt;&lt;span  fontweight='bold'&gt; larynx*&lt;/span&gt;&lt;span &gt; en dessous, qui correspond a l'entree dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;trachee*&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;le pharynx* &lt;/span&gt;&lt;span &gt;en arriere qui se poursuit vers le bas par l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;œsophage*&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut noter que l'ensemble de ces structure est tapissee de &lt;/span&gt;&lt;span  fontweight='bold'&gt;muqueuse*&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur le plan fonctionnel, il s'agit d'un carrefour. ou le flux d'air (inspire/expire) va croiser le bol alimentaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l '&lt;/span&gt;&lt;span  fontweight='bold'&gt;epiglotte*&lt;/span&gt;&lt;span &gt; va assurer la fermeture de la trachee au moment de la deglutition. le bol alimentaire degluti va poursuivre sa course dans l'œsophage.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de meme , mais vers le haut,  le &lt;/span&gt;&lt;span  fontweight='bold'&gt;voile du palais*&lt;/span&gt;&lt;span &gt; qui correspond a la partie posterieure mobile du voile. va fermer les fosses nasales au moment de la deglutition.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
