SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la vascularisation arterielle a une disposition et une origine differente au niveau de la machoire sup et de la machoire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voyons dans un premier temps la machoire superieure:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la vascularisation arterielle est assuree par des branche de &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'artere maxillaire*&lt;/span&gt;&lt;span &gt;  qui est une branche terminale de la carotide externe.  elle va donner:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;l'artere alveolaire superieure et posterieur*&lt;/span&gt;&lt;span &gt;; elle penetre l'os maxillaire et descend dans la paroi osseux de ce dernier. elle forme des &lt;/span&gt;&lt;span  fontweight='bold'&gt;rameaux dentaires*&lt;/span&gt;&lt;span &gt;, qui s'anastomosent, d'ou parte les &lt;/span&gt;&lt;span  fontweight='bold'&gt;rameaux radiculaire*&lt;/span&gt;&lt;span &gt; destines aux molaires et le premolaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;l'artere sous-orbitaire* &lt;/span&gt;&lt;span &gt;ne du coude terminal de l'artere maxillaire et traverse de par en par le canal sous-orbitaire. dans le canal elle donne l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;artere alveolaire anterieure*&lt;/span&gt;&lt;span &gt; qui a travers son propre canal va donner des rameaux dentaires qui s'anastomosent entre eux et avec le reseau de l'artere alveolaire  superieure et posterieur pour donner des &lt;/span&gt;&lt;span  fontweight='bold'&gt;rameaux radiculaires*&lt;/span&gt;&lt;span &gt; destines aux canines et aux incisives.  l'anastomose entre les rameaux dentaires forme une veritable &lt;/span&gt;&lt;span  fontweight='bold'&gt;arcade anastomotique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;note : il existe des &lt;/span&gt;&lt;span  fontweight='bold'&gt;arteres alveolaires moyennes*&lt;/span&gt;&lt;span &gt;  inconstantes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
