SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;tout d'abord il est important de s'entendre sur ce qu'est une eradication&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;entre 1997 et 2010 une tentative de distinction entre eradication et elimination a ete faite par l 'oms &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le terme eradication sous entendant a l'echelle de la planete. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; les deux termes eradication et elimination font reference aux disparitions de ces maladies suite a des programmes de controle et des campagnes, ne sont pas des extinctions naturelles des infections &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du fait de la persistance d'un confusion importante car certains parlaient encore d'eradication globale ou mondiale ou parlaient d'eradication alors la maladie n'a pas totalement disparue en 2010 il a ete decide de ne parler plus que d'eradication et de preciser a chaque fois dans quelle zone geographique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
