SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;je voudrais detailler quelques infections candidates  comme le paludisme &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus complique car pas de vaccin et vecteur tres mobile et population infectee tres importante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les campagnes passees ont ete des echecs. un programme d'envergure a ete lance par l'oms dans les annees 50 puis renouvele 10ans et 20 ans plus tard avec un echec. du coup on ne parlait plus d'eradication mais de contr^mle. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;des resultats majeurs de ce controle ont ete obtenus ces 15 dernieres annees avec une &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;baisse de la mortalite  de 57 % (de 1 m a 631 000 cas de deces par an) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;baisse de la mortalite des enfants de moins de 5 ans de 80 % a 40 % &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;efficacite des campagnes de masse pour ameliorer le &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;diagnostic rapide &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;traitement avec des combinaisons a base d'arteminsinine &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;moustiquaires impregnees&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du coup l'espoir de l'eradication renait meme si apparition de res aux insecticides et aux traitements &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
