SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le virus de la variole est de la famille des poxvirus et s'appelle le smallpox. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce virus est tres resistant et il est proche des virus animaux comme celui de la vaccine qui infecte les bovins et les chevaux. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la variole etait caracterisee par une contagiosite majeure avec le malade contagieux des l'apparition des premiers boutons et meme un peu avant et jusqu'a disparition complete des croutes ce qui pouvait durer jusqu'a 3 semaines. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'infection etait contagieuse par contact direct avec les lesions du patient mais aussi par voie indirecte par contact avec des objets ou des vetements ou la literie que le patient avait touche puisque le virus resiste tres bien dans le milieu exterieur.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
