// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le programme d'eradication de la variole @";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide9.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un seul programme d'eradication suffira pour la variole il est entrepris par l'oms en 1967 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le tableau detaille le nombre de cas recenses en 1974 par l'oms. ce que l'on peut remarquer c'est que le nombre de cas est  encore considerable, notamment en asie.  ces chiffres ne sont bien entendu pas a retenir&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le cas de la variole : les conditions favorables pour l'eradication :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pas de reservoir animal de virus&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;existence d'un vaccin efficace qui confere une protection absolue quoique non definitive et bien  supporte&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
