SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le deuxieme exemple d'eradication des maladies infectieuses est celui de la poliomyelite qui  montre que l'objectif fixe par l'oms d'obtenir  une eradication globale en 2000 n'a pas ete atteint et que meme on observe une aggravation de la situation depuis cette date qui peut remettre en cause l'eradication globale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette carte est la juste pour illustrer la situation recente avec un nombre important de cas en inde, au pakistan et au nigeria&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les chiffres ne sont pas a retenir. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
