SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;liee au poliovirus qui est virus a arn du genre enterovirus cad virus a transmission digestive. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la maladie est strictement humaine, pas de reservoir animal, mais le virus peut persister dans l'environnement plusieurs semaines &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le virus est tres resistant dans le milieu exterieur. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;donc la transmission est surtout directe (oro-fecale) mais peut etre indirecte (eau ou aliments souilles)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;virus neurotrope qui entraine des paralysies flasques cad avec hypotonie musculaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avant le programme d'eradication on deplorait 500 000 nouveaux d'infirmites liees a la polio touchant principalement les enfants dans les pvd / an &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on definit egalement le syndrome post poliomyelitique qui  correspond a une reprise evolutive apres l'episode aigu&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
