SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le risque de reexpansion mondiale est  majeur au nigeria et en inde &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par exemple au nigeria on a observe entre 2000 et 2003 un retour explosif avec reexpension a d'autres pays africains et asiatiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de plus tres inquietant la reapparition en chine en 2011 alors que la maladie avait disparu de ce pays depuis 1999&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la chine a ete contaminee par une souche circulant au pakistan&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quand on connait l'expansion demographique de ces pays c'est effectivement tres inquietant pour le controle de la polio&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
