SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'oms insiste actuellement sur la necessite de renforcer le programme et de finir le travail de l'eradication sinon la &lt;/span&gt;&lt;span &gt;reexpension&lt;/span&gt;&lt;span &gt; sera trop importante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'accent est mis sur la surveillance avec declaration obligatoire et surveillance virologique &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pfa : paralysie flasque (avec hypotonie musculaire) d'installation rapide (mois de 3 jours)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;elements encourageants :   en 2012 plus que 5 pays avec transmission detectee&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nigeria; tchad, afghanistan, pakistan, rdc&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;mars 2013 : plus que 3 pays &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nigeria, afghanistan, pakistan, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;octobre 2015 : 0 cas au nigeria &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;aout 2016 : reemergence nigeria apres 2 ans sans nouveaux cas &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans le nord du nigeria : region controlee par &lt;/span&gt;&lt;span &gt;boko&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;haram&lt;/span&gt;&lt;span &gt; ou la couverture vaccinale est insuffisante &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au pakistan instauration tres recente d'une taxe sur le vaccin qui etait gratuit jusque la &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
