SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le troisieme exemple est la dracunculose ou ver de guinee (guinea worm en anglais) ou filaire de medine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'eradication globale est maintenant presque atteinte. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a la difference des 2 precedentes qui etaient des infections virales ici c'est un parasite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une des parasitoses les plus anciennement connue :meme si elle existait avant jc,  les premieres descriptions les plus indiscutables et precises datent des civilisations grecques et romaines au 1&lt;/span&gt;&lt;span &gt;er&lt;/span&gt;&lt;span &gt; siecle apres jc &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les premieres illustrations datent du 17  eme, celle-ci etant du 19eme&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;parasitose qui marque l'imaginaire de part ces manifestations impressionnantes mais qui reste benigne. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
