SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'eradication de la dracunculose est jugee possible et relativement facile car meme si il n'y a pas de vaccin ni de traitement preventif plusieurs arguments plaident en faveur d'une eradication facile&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;seul l'homme permet d'achever le cycle et de le boucler. la transmission passe par lui&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le vecteur (le cyclops) a une localisation relativement stable dans l'eau douce (non mobile par opposition aux moustiques) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;incubation limitee (1 an)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;diagnostic facile sans ambiguite par l'emergence du ver&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;distribution limitee a un nombre de pays faible &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette eradication est en plus jugee prioritaire car permettra d'ameliorer l'acces a l'eau  potable&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;	impact sur d'autres infections a transmission hydrique comme par exemple les diarrhees&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;2&lt;/span&gt;&lt;span &gt;eme&lt;/span&gt;&lt;span &gt; infection pouvant etre eradiquee de la totalite du globe apres la variole eradiquee en 1977 &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour interrompre le cycle : deux grands moyens &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;eviter la contamination de l'eau en evitant les contacts entre les menbre infs des hommes et l'eau de boisson&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et eviter la contamination de l'homme en eliminant les cyclpos de l'eau de boisson &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;20/09/2018&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
