SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bonjour, dans la&lt;/span&gt;&lt;span &gt; sequence precedente, nous avons detailler des demarches collective et interprofessionnelles permettant d'analyser la survenue des erreurs medicamenteuses. l'objectif de cette analyse est de definir un plan d'action visant a eviter la reproduction de l'erreur medicamenteuse. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
