SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous reconnaissez le modele de &lt;/span&gt;&lt;span &gt;reason&lt;/span&gt;&lt;span &gt; que nous avons detaille dans le precedent chapitre. pour rappel,&lt;/span&gt;&lt;span &gt; l&lt;/span&gt;&lt;span &gt;'origine des erreurs medicamenteuses est multifactorielle et concerne les differents acteurs intervenant dans les etapes de ce circuit. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
