SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les &lt;/span&gt;&lt;span &gt;defaillances latentes &lt;/span&gt;&lt;span &gt;sont lies aux defaillances qui concernent en particulier le management, l'organisation, les processus et procedures, les equipements materiels, la formation des acteurs et leurs competences, l'encadrement et l'existence de facteurs contributifs et de precurseurs psychologiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
