SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les defaillances patentes &lt;/span&gt;&lt;span &gt;sont quant a elles liees a la prise de risque dans les pratiques mais aussi a l'absence ou l'inadequation des defenses des systemes de protection. l'operateur est donc en premiere ligne.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
