SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous introduisons ici une nouvelle notion: les risques averes. il y&lt;/span&gt;&lt;span &gt; a des situations cliniques pour lesquelles les soignants sont dans l'obligation de prendre des decisions ou realiser des actes qui vont favoriser la survenue ou l'expression d'un evenement indesirable pour le patient. ce sont les risques averes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
