SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour ces 3 categories de risques,&lt;/span&gt;&lt;span &gt; il existe un type de barriere specifiques, celles que nous avons detailler en ouverture de cette sequence.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;reposant sur la construction de barrieres pour eviter la survenue d'erreurs analogues,&lt;/span&gt;&lt;span &gt; la prevention va plus particulierement prendre en charge les defaillances latentes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la recuperation va quant a elle correspondre aux risques patents puisqu'il s'agit du &lt;/span&gt;&lt;span &gt;depistage et du traitement d'un dysfonctionnement existant. pour rappel, les defaillances patentes sont des erreurs actives ,visibles&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;immediatement, de l'acteur en 1re ligne directement liee a l‘erreur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, l'attenuation est la mise en place de mesures qui reduisent les consequences d'un risque avere et realise.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
