SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;concernant la securisation organisationnelle du circuit du medicament, des moyens de securisation ont ete developpes recemment, comme par exemple, la distribution automatisee des medicaments, la delivrance journaliere nominative, les armoires securisees, ou encore la &lt;/span&gt;&lt;span &gt;pharmacotechnie&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
