SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;concernant les pratiques professionnelles, nous aborderons les concepts d'analyse pharmaceutique et du suivi therapeutique des patients notamment par l'education therapeutique, qui representent des pistes majeures de developpement actuellement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
