SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant de detailler plus en avant les activites pharmaceutiques&lt;/span&gt;&lt;span &gt; destinees a prevenir l'iatrogenie medicamenteuse, j'aimerais vous presenter de maniere schematique l'organisation d'une pharmacien en milieu hospitalier. je pense que vous maitrisez un peu mieux l'organisation en ville. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pharmacie&lt;/span&gt;&lt;span &gt; de l'hopital est constituee d'une pharmacie centrale. elle est souvent situee dans un pavillon annexe ou au sous-sol de l'hopital. c'est notamment dans ce leu que sont stockes les medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
