SDpagedisplay[SDpagenum] = "Slide16.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est possible de rencontrer&lt;/span&gt;&lt;span &gt; parfois des antennes pharmaceutiques. &lt;/span&gt;&lt;span &gt;une antenne pharmaceutique est une pharmacie d'etage localisee a proximite des unites de soins. &lt;/span&gt;&lt;span &gt;ce sont des « pharmacies d'etage » comprenant un stock de medicaments limite et adapte a l'unite de soins aupres duquel elle est situe. &lt;/span&gt;&lt;span &gt;elle integre un preparateur en pharmacie charge des approvisionnements, de la gestion, et de la preparation individuelle journaliere des doses a administrer. cette organisation permet d'assurer une delivrance avec une grande reactivite, en fonction des modifications de prescription, du fait de la proximite entre les preparateurs en pharmacie et le personnel infirmier. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

