SDpagedisplay[SDpagenum] = "Slide17.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;qu'en est-il maintenant des modes de delivrance. la delivrance correspond&lt;/span&gt;&lt;span &gt; a l'ensemble des actes de distribution d'un medicament ou d'un dispositif medical assures par un pharmacien ou un preparateur en pharmacie a partir de la reception d'une prescription, comportant la collecte du medicament, sa repartition et sa remise aux unites de soins ou au patient. la delivrance globale correspond a la livraison du commande globale a l'unite de soins. en fonction de leurs besoins et du stock de leur dotation, les soignants passent commande des medicaments dont ils ont besoin. a une frequence definie, la pharmacie centrale livre les caisses de medicaments. charge ensuite aux infirmiers de preparer les traitements pour chaque patient au moment de leur tournee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
