SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;par opposition, il est possible d'organiser la delivrance nominative. au lieu d'une distribution «en vrac» des medicaments aux unites de soins, la pharmacie delivre des doses individualisees chaque jour ou chaque semaine a partir d'une ordonnance individuelle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette delivrance nominative peut etre manuelle ou automatisee.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
