SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les resultats sur objectifs du caqes font l'objet d'une evaluation annuelle qui sont apprecies de maniere globale par l'ars et l'assurance maladie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'evaluation peut conduire a la notification de sanctions ou au declenchement d'un interessement pour les volets additionnels transport et pertinence des soins.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le pharmacien hospitalier&lt;/span&gt;&lt;span &gt; est investi sur l'ensemble des ces points. neanmoins, &lt;/span&gt;&lt;span &gt;dans cette sequence,&lt;/span&gt;&lt;span &gt; n&lt;/span&gt;&lt;span &gt;ous allons nous interesser&lt;/span&gt;&lt;span &gt; particulierement au 2 premiers points.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
