SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le volet obligatoire&lt;/span&gt;&lt;span &gt; regroupe un ensemble d'objectifs correspondant a 5 grandes thematiques qui sont:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'amelioration et securisation de la prise en charge medicamenteuse et du circuit du medicament et des produits et prestations,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le developpement des pratiques pluridisciplinaires ou en reseau, deploiement de la pharmacie clinique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la promotion de la prescription des produits du repertoire des generiques ou &lt;/span&gt;&lt;span &gt;biosimilaires&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les engagements relatifs aux depenses pour les prescriptions hospitalieres dispenses en ville&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et enfin les engagements relatifs aux depenses et au respect des referentiels pour les medicaments et dispositifs medicaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
